import { useEffect, useRef, useState } from "react"
import { useLocation } from "react-router"
import {decodeJwtToken, date} from "../Utils/function"
import io from 'socket.io-client'
import { Container, Row, Form, Button} from "react-bootstrap"
import {getRoom} from "../DAL/roomsApi"
import {BiMessageDetail} from "bi"


let socket 

function Room(){

    const myRef = useRef(null)

    const ENDPOINT = "localhost:3100"
    const {state} = useLocation()
    const {id, userName} = decodeJwtToken()

    const [room, setRoom] = useState(state)
    const [name, setName] = useState(userName)

    const [message, setMessage] = useState('')
    const [messages, setMessages] = useState([])

    const executeScroll = () =>{
        myRef.current.scrollIntoView({behavior: "smooth"})
    } 


    useEffect(()=>{executeScroll()},[messages])

    useEffect(async()=>{
        const roomDetails = await getRoom(state._id)
        setRoom(roomDetails)  
        setMessages(roomDetails.messages) 
    }, [])

    useEffect(()=>{
        socket = io(ENDPOINT)
        socket.emit('join', {id, name, room}, ()=>{
        })

        return () => {
            socket.emit('disconnection', {id, name, room}, ()=>{
            })
            socket.off()
        }
    }, [])

    useEffect(() => {
        socket.on('message', ({message}) => {
            setMessages(messages => [ ...messages, message ])
        })
    }, [])



    function sendMessage(){
        if(message){
            socket.emit('sendMessage', {name, room, message}, ()=>{
            })
            setMessage("")
        }
    }


    return <>
        <Container>
            <Row className="justify-content-center mt-4">
                <div className="chat-header">
                    <h4 className="text-center mt-2">{room.roomName}</h4>
                </div>


                <div className="chat-body">

                    <div className="chat-side-bar">
                        <h5>Room Name:</h5>
                    </div>

                    <div className="chat-message-textArea">
                        <div className="chat-messages">
                            {messages.map((message, index)=>{
                                return  <div key={index} className={`chat-out ${message.user === name ? "left" : "right" }`}> 
                                            <span className={`chat-rectangle ${message.user === name ? "my-color" : "other-color" }`}>
                                                {message.user&&<p className="chat-rectangle-name-date"> <span>{message.user}</span> <span>{date(message.date)}</span></p>}
                                                <p className="chat-rectangle-message">{message.text} </p>
                                            </span>
                                        </div> 
                            })}
                            <span ref = {myRef}></span>
                        </div>

                        <div className="textarea-message" mb-3 mt-4 >
                            <textarea className="textArea" rows="2" value={message} onChange={(e)=>setMessage(e.target.value)}/>
                            <button className="btn-message" variant="primary" onClick = {sendMessage}>Send</button>
                        </div>
                    </div>
                    

                </div>


                <div className="chat-footer">
                    <p>Admin : {room.admin.name}</p>
                </div>
            </Row>

        </Container>
    </>
}

export default Room
