import {Navbar, Nav} from "react-bootstrap"
import {NavLink, useHistory} from "react-router-dom"
import { useSelector, useDispatch} from "react-redux"
import {logoutUser} from "../DAL/authenticationApi"

function Bar(){

    const history = useHistory()
    const dispatch = useDispatch()
    const isLoggedIn = useSelector(state => state.isLoggedIn)

    const logout = () => {
        dispatch({type: 'LOGOUT'})
    }


    function toLogout(){
        logout()
        history.push('/')
        logoutUser()
    }

    return <>
    <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark" className="Nav-Bar">
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />

        <Navbar.Collapse id="responsive-navbar-nav">
            {!isLoggedIn&&<Nav>
                <NavLink to="register" className="nav-link">Register</NavLink>
                <NavLink to="login" className="nav-link">Login</NavLink>
            </Nav>}

            {isLoggedIn&&<Nav>
                <NavLink to="/" className="nav-link " onClick = {()=>toLogout()}>Logout</NavLink>
            </Nav>}
        </Navbar.Collapse>

        {isLoggedIn&&<Navbar.Collapse id="responsive-navbar-nav">
            <NavLink to="/rooms" className="nav-link nav-auth">Chat Rooms</NavLink>
            <NavLink to="/createRoom" className="nav-link nav-auth">Create Room</NavLink>
        </Navbar.Collapse>}
    </Navbar>
    </>
}

export default Bar
