import { useEffect, useState } from "react"
import { useLocation } from "react-router"
import {decodeJwtToken} from "../Utils/function"
import io from 'socket.io-client'
import { Container, Row, Form, Button} from "react-bootstrap"
import {getRoom, getMessages} from "../DAL/roomsApi"



let socket 

function Room(){

    const ENDPOINT = "localhost:3100"
    const {state} = useLocation()
    const {id, userName} = decodeJwtToken()

    const [room, setRoom] = useState(state)
    const [name, setName] = useState(userName)

    const [message, setMessage] = useState('')
    const [messages, setMessages] = useState([])

    useEffect(async()=>{
        let msg = await getMessages(state._id)
        msg = msg.map((value) => {
            if(msg.name === name){
                msg.side = 'left'
                msg.color = 'my-message'
            }
        })
        setMessages(msg)

        const roomDetails = await getRoom(state._id)
        setRoom(roomDetails)
        
    }, [])

    useEffect(()=>{
        socket = io(ENDPOINT)
        socket.emit('join', {id, name, room}, ()=>{

        })

        return () => {
            socket.emit('disconnection', {id, name, room}, ()=>{

            })
            socket.off()
        }

    }, [ENDPOINT])



    useEffect(() => {
        socket.on('message', ({message}) => {

            if(message.user && message.user === name){
               message.color = "my-message" 
               message.side = 'left'

            } else {
                message.color = "other-message" 
                message.side = 'right'
            }
           
            setMessages(messages => [ ...messages, message ])
        })
        
    }, [])


    function sendMessage(){
        if(message){
            socket.emit('sendMessage', {name, room, message}, ()=>{

            })
            setMessage("")
        }
    }


    return <>
        <Container>
            <Row className="justify-content-center mt-4">
                <div className="chat-header">
                    <h4 className="text-center mt-2">{room.roomName}</h4>
                </div>

                <div className="chat-body">
                    {messages.map((message, index)=>{
                        return  <div key={index} className={`chat-out ${message.side}`}> 
                                    <span className={`chat-rectangle ${message.color}`}>
                                        {message.user&&<p className="chat-rectangle-name">{message.user} </p>}
                                        <p className="chat-rectangle-message">{message.text} </p>
                                    </span>
                                </div>
                    })}
                </div>

                <div className="chat-footer">
                    <p>Admin : {room.admin.name}</p>
                </div>
            </Row>

            <Row className="justify-content-center">
                <Form.Group className="mb-3 mt-4 textarea-message" controlId="exampleForm.ControlTextarea1">
                    <Form.Control as="textarea" value={message} rows={3} onChange={(e)=>setMessage(e.target.value)} />
                </Form.Group>
            </Row>

            <Row className="justify-content-center">
               <Button className="btn-message" variant="dark" onClick = {sendMessage}>Send</Button>
            </Row>
        </Container>
    </>
}

export default Room
