import { useEffect, useState } from "react"
import { getRooms, getRoomsBySearch } from "../DAL/roomsApi"
import { Container, Row, Col } from "react-bootstrap"
import CardRoom from "../Components/CardRoom"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faSearch } from '@fortawesome/free-solid-svg-icons'

function Rooms(){

    const [rooms, setRooms] = useState([]) // id, roomName, admin, roomType
    const [searchRoom, setSearchRoom] = useState('') // id, roomName, admin, roomType

    useEffect(async()=>{
        const publicRooms = await getRooms()
        setRooms(publicRooms)
    },[])

    useEffect(()=>{
        const timeToOpreate = setTimeout(async()=>{
            if(searchRoom !== ''){
                const search = await getRoomsBySearch(searchRoom)
            }
        }, 1000)

        return () => {
            clearTimeout(timeToOpreate)
        }
    }, [searchRoom])


    return <>
        <h1 className ="header text-center">Chat Rooms</h1>
        <Container>
            <div className="my-4 d-flex justify-content-center">
                <div className="search-rooms d-flex col-10 col-lg-4 col-md-6">
                    <input className="search-input" type='text' placeholder="Search Room" onChange={(e)=>setSearchRoom(e.target.value.trim().t)}></input>
                    <FontAwesomeIcon icon={faSearch} className="icon-search"/>
                </div>
            </div>

            <Row>
                {rooms.map((room, index) =>
                {return <Col key={index} xs={8} md={6} lg={4} className="mb-3 mx-auto mx-md-0">
                    <CardRoom room = {room}/>
                </Col>})}
            </Row>
        </Container>
    </>
}

export default Rooms
