import { useEffect, useRef, useState } from "react"
import { useLocation } from "react-router"
import {decodeJwtToken, date} from "../Utils/function"
import io from 'socket.io-client'
import { Container, Row} from "react-bootstrap"
import {getMessges, getUsers} from "../DAL/roomsApi"
import {BiMessageDetail} from "react-icons/bi"
import {FaUsers} from "react-icons/fa"


let socket 

function Room(){

    const bottomScroll = useRef(null)

    const ENDPOINT = "localhost:3100"
    const {state} = useLocation()
    const {id, userName} = decodeJwtToken()

    const [room, setRoom] = useState(state) // id, roomName, admin, roomType
    const [name, setName] = useState(userName) // userName

    const [usersRoom, setUsersRoom] = useState([])
    const [message, setMessage] = useState('') 
    const [messages, setMessages] = useState([])
    const [skip, setSkip] = useState(0)

    const executeScroll = () =>{
        bottomScroll.current.scrollIntoView()
    } 

    const  handleScroll = e => {
        if (e.target.scrollTop == 0) {
            setSkip(skip+1)
        }
     }

    useEffect(()=>{ executeScroll() },[])


    useEffect(async()=>{
        const messagesHistory = await getMessges(room.id, skip) 
        setMessages([...messagesHistory, ...messages]) 
    }, [skip])


    useEffect(async()=>{
        getListUsers()
    }, [])


    useEffect(()=>{
        socket = io(ENDPOINT)
        socket.emit('join', {id, name, room}, ()=>{
        })

        return () => {
            socket.emit('disconnection', {id, name, room}, ()=>{
            })
            socket.off()
        }
    }, [])

    useEffect(() => {

        socket.on('message', ({message}) => {

            if(message.user === "") {  //user joined or left
                getListUsers()
            }
            setMessages(messages => [ ...messages, message ])
            executeScroll()
        })
    }, [])



    async function getListUsers(){
        const users = await getUsers(room.id) 
        setUsersRoom(users) 
    }

    function sendMessage(){
        if(message){
            socket.emit('sendMessage', {name, room, message}, ()=>{
            })
            setMessage("")
        }
    }


    return <>
        <Container>
            <Row className="justify-content-center mt-4">
                <div className="chat-header">
                    <h4 className="text-center mt-2">Chat Room</h4>
                </div>


                <div className="chat-body">

                    <div className="chat-side-bar">
                        <div className="name-room">
                            <BiMessageDetail size={25} className="icon-msg"/> <h6>Room Name :</h6>
                        </div>

                        <div className="header-room">
                            <h4 className="text-center">{room.roomName}</h4>
                        </div>

                        <div>
                            <div className="name-room mb-2">
                                <FaUsers size={25} className="icon-msg"/>
                                 <h6>Users :</h6>
                            </div>
                            <div className="users-room">
                                {usersRoom.map((user, index)=> <p key = {index}>{user.userName}</p>)}
                            </div>
                        </div>
                    </div>

                    <div className="chat-message-textArea">
                        <div className="chat-messages" onScroll={(e)=>handleScroll(e)}>
                            {messages.map((message, index)=>{
                                return  <div key={index} ref={index} className={`chat-out ${message.user === name ? "left" : "right" }`}> 
                                            <span className={`chat-rectangle ${message.user === name ? "my-color" : "other-color" }`}>
                                                {message.user&&<p className="chat-rectangle-name-date"> <span>{message.user}</span> <span>{date(message.date)}</span></p>}
                                                <p className="chat-rectangle-message">{message.text} </p>
                                            </span>
                                        </div> 
                            })}
                            <span ref = {bottomScroll}></span>
                        </div>

                        <div className="textarea-message">
                            <textarea className="textArea" rows="2" value={message} onChange={(e)=>setMessage(e.target.value)}/>
                            <button className="btn-message" variant="primary" onClick = {sendMessage}>Send</button>
                        </div>
                    </div>
                    
                </div>


                <div className="chat-footer">
                    <p>Admin : {room.admin.name}</p>
                </div>
            </Row>
        </Container>
    </>
}

export default Room
