import {createStore} from "redux"


const initialStates = {IsLoggedIn:0, }

const loginHandler = (state = {IsLoggedIn: false}, action) =>{
    if (action.type === 'login'){
        return {
            IsLoggedIn: true
        }
    }

    if (action.type === 'logout'){
        return {
            IsLoggedIn: false
        }
    }

    return state    
}

const spinnerHandler = (state = {spinner: false}, action) =>{
    if (action.type === 'loading'){
        return {
            spinner: true
        }
    }

    if (action.type === 'start'){
        return {
            spinner: false
        }
    }

    return state    
}


const store = createStore(loginHandler, spinnerHandler)

export default store
