import { useEffect, useState } from "react"
import { useLocation } from "react-router"
import {decodeJwtToken} from "../Utils/function"
import io from 'socket.io-client'
import { Container, Row, Form, Button} from "react-bootstrap"

let socket

function Room(){

    const ENDPOINT = "localhost:3100"
    const {state} = useLocation()
    const {userName} = decodeJwtToken()

    const [room, setRoom] = useState(state)
    const [name, setName] = useState(userName)
    
    const [colorMessage, setColorMessage] = useState(userName)
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);

    useEffect(()=>{

        socket = io(ENDPOINT)
        console.log(socket)
        console.log(room)
        console.log(name)
        socket.emit('join', {name, room}, ()=>{

        })

        return () => {
            socket.emit('disconnection')
            socket.off()
        }

    }, [ENDPOINT])


    useEffect(() => {
        socket.on('message', ({message}) => {

            message.name === name ? message.colorMessage = "my-message" : message.colorMessage = "Other messages"

            setMessages(messages => [ ...messages, message ]);
        })
        
    }, [])


    function sendMessage(){
        if(message){
            socket.emit('sendMessage', {message, room, name}, ()=>{

            })
            setMessage("")
        }
    }


    return <>
        <Container>
            <Row className="justify-content-center mt-4">
                <div className="chat-header">
                    <h4 className="text-center mt-2">{room.roomName}</h4>
                </div>

                <div className="chat-body">
                    {messages.map((message, index)=>{
                        return  <div  key={index} className="chat-rectangle"> 
                                    {message.user&&<p className="chat-rectangle-name">{message.user} </p>}
                                    <p className="chat-rectangle-message">{message.text} </p>
                                </div>
                    })}
                </div>

                <div className="chat-footer">
                    <p>Admin : {room.admin.name}</p>
                </div>
            </Row>

            <Row className="justify-content-center">
                <Form.Group className="mb-3 mt-4 textarea-message" controlId="exampleForm.ControlTextarea1">
                    <Form.Control as="textarea" value={message} rows={3} onChange={(e)=>setMessage(e.target.value)} />
                </Form.Group>
            </Row>

            <Row className="justify-content-center">
               <Button className="btn-message" variant="dark" onClick = {sendMessage}>Send</Button>
            </Row>
        </Container>
    </>
}

export default Room
