import { createStore } from "redux"

const initialStates = { isLoggedIn: 0 }

const myStates = (state = initialStates, action) => {
  if (action.type === "LOGIN") {
    return {
      ...state,
      IsLoggedIn: true,
    }
  }

  if (action.type === "LOGOUT") {
    return {
      ...state,
      IsLoggedIn: false,
    }
  }

  if (action.type === "LOADING") {
    return {
      ...state,
      spinner: true,
    }
  }

  if (action.type === "START") {
