import { useEffect, useState } from "react"
import { useLocation } from "react-router"
import io from 'socket.io-client'
import { Container, Row, Form, Button} from "react-bootstrap"

let socket

function Room(){

    const ENDPOINT = "localhost:3100"
    const {state} = useLocation()
    const [room, setRoom] = useState(state)
    const [name, setName] = useState("david")

    const [users, setUsers] = useState('');
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);

    useEffect(()=>{

        socket = io(ENDPOINT)
        console.log(socket)
        console.log(room)
        socket.emit('join', {name, room}, ()=>{

        })

        return () => {
            socket.emit('disconnection')
            socket.off()
        }

    }, [ENDPOINT])


    useEffect(() => {
        socket.on('message', ({message}) => {
            setMessages(messages => [ ...messages, message ]);
        })
        
        socket.on("roomData", ({ users }) => {
            setUsers(users);
        })
    }, [])


    function sendMessage(){
        if(message){
            socket.emit('sendMessage', message)
            setMessage("")
        }
    }


    return <>
        {console.log(messages)}
        <Container>
            <Row className="justify-content-center mt-4">
                <div className="chat-header">
                    <h4 className="text-center mt-2">{room.roomName}</h4>
                </div>

                <div className="chat-body">
                    {messages.map((message, index)=>{
                        return <p key ={index}>{message}</p>
                    })}
                </div>

                <div className="chat-footer">
                    <p>Admin : {room.admin.name}</p>
                </div>
            </Row>

            <Row className="justify-content-center">
                <Form.Group className="mb-3 mt-4 text-message" controlId="exampleForm.ControlTextarea1">
                    <Form.Control as="textarea" rows={3} onChange={(e)=>setMessage(e.target.value)} />
                </Form.Group>
            </Row>

            <Row className="justify-content-center">
               <Button className="btn-message" variant="dark" onClick = {sendMessage}>Send</Button>
            </Row>
        </Container>
    </>
}

export default Room
