const baseUrl = "http://localhost:3100"



async function createNewRoom(room){ // add inMemoryToken
    const response = await fetch(`${baseUrl}/rooms`, {
        method: 'POST', 
        credentials: 'include', 
        headers: {'Content-Type': 'application/json'}, 
        body: JSON.stringify(room)
    })
    return response
}

async function getPublicRooms(){
    const response = await fetch(`${baseUrl}/rooms`)
    return response.json()
}

async function getMessges(id, skip){
    const response = await fetch(`${baseUrl}/rooms/messages/?id=${id}&skip=${skip}`)
    return response.json()
}

async function getUsers(id){
    const response = await fetch(`${baseUrl}/rooms/users/${id}`)
    return response.json()
}

async function changeMessagesViewed(messagesId){
    console.log(messagesId);
    const response = await fetch(`${baseUrl}/rooms/changeViewed`, {
        method: 'PUT', 
        headers:{'Content-Type' : 'appliction/json'}, 
        credentials: 'include', 
        body: JSON.stringify(messagesId)
    })
    return response.json()
}


export {createNewRoom, getPublicRooms, getMessges, getUsers, changeMessagesViewed}
